import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{r as n,o as l,c as o,a,b as e,d as i,f as r}from"./app-6b49bbd8.js";const c={},p=r(`<h1 id="julia换源-更换国内镜像站" tabindex="-1"><a class="header-anchor" href="#julia换源-更换国内镜像站" aria-hidden="true">#</a> Julia换源(更换国内镜像站)</h1><p>!!! tip<br> Contents：JuliaPkg</p><pre><code>Contributor: YJY

Email:522432938@qq.com

如有错误，请批评指正。
</code></pre><ol><li><p>打开julia的安装路径，找到文件夹 julia-1.x.x(如1.6.1) 如图<br><img src="https://img-blog.csdnimg.cn/303b28feddd74bbb97ea3ab07dd9183c.png" alt="在这里插入图片描述" loading="lazy"></p></li><li><p>进入该文件夹找到etc<br><img src="https://img-blog.csdnimg.cn/5728ccd73bd14b27a1848b4d5291048f.png?x-oss-process=image/watermark,type_ZHJvaWRzYW5zZmFsbGJhY2s,shadow_50,text_Q1NETiBAamFrZTQ4NA==,size_20,color_FFFFFF,t_70,g_se,x_16" alt="在这里插入图片描述" loading="lazy"></p></li><li><p>进入etc 再进入一个文件夹，有一个startup.jl<br><img src="https://img-blog.csdnimg.cn/5288a19b5c8445b78cddc8bd9bc6448d.png" alt="在这里插入图片描述" loading="lazy"></p></li></ol><p><strong>这个startup.jl就很方便了，在启动julia的时候，会首先自动运行里面的内容</strong>。</p><p>所以，打开它，在里面输入一行</p><div class="language-julia line-numbers-mode" data-ext="julia"><pre class="language-julia"><code>ENV<span class="token punctuation">[</span><span class="token string">&quot;JULIA_PKG_SERVER&quot;</span><span class="token punctuation">]</span><span class="token operator">=</span><span class="token string">&quot;https://mirrors.pku.edu.cn/julia/&quot;</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>就像这样<br><img src="https://img-blog.csdnimg.cn/95077d4c254942c2b637d4f926e652ed.png" alt="在这里插入图片描述" loading="lazy"><br> 保存就完事了。</p><p>ENV是julia的全局环境变量，能够直接在repl中修改环境变量，但不是永久的。所以放在startup.jl里。这样等价于永久改变了，因为每次启动都会repl自动执行它。</p><p>在repl中输入ENV如下：<br><img src="https://img-blog.csdnimg.cn/b2ff78c2cd40404bbc4ab667ae11c241.png?x-oss-process=image/watermark,type_ZHJvaWRzYW5zZmFsbGJhY2s,shadow_50,text_Q1NETiBAamFrZTQ4NA==,size_20,color_FFFFFF,t_70,g_se,x_16" alt="在这里插入图片描述" loading="lazy"></p>`,10),d={href:"https://docs.juliacn.com/latest/manual/environment-variables/",target:"_blank",rel:"noopener noreferrer"},_=a("br",null,null,-1),g=a("img",{src:"https://img-blog.csdnimg.cn/2c1e899b55884b6eb0b0afb7df788d5d.png?x-oss-process=image/watermark,type_ZHJvaWRzYW5zZmFsbGJhY2s,shadow_50,text_Q1NETiBAamFrZTQ4NA==,size_20,color_FFFFFF,t_70,g_se,x_16",alt:"在这里插入图片描述",loading:"lazy"},null,-1),m=a("p",null,"PS: 不换也行，最新版的julia好像能自动选取一个最快的源。",-1);function u(b,h){const s=n("ExternalLinkIcon");return l(),o("div",null,[p,a("p",null,[e("还有一些其它的比如LOAD_PATH 和DEPOT_PATH等等，这些都是环境变量。更多可以看"),a("a",d,[e("中文文档"),i(s)]),_,g]),m])}const x=t(c,[["render",u],["__file","julia_change_pkgserve.html.vue"]]);export{x as default};
